import React, { useState, useEffect } from "react";
import { Navbar, Nav, Container, NavDropdown } from "react-bootstrap";
import { NavLink } from "react-router-dom";
import "./Navbar.css";
import Logo from "../../Assets/Logo.svg";



import { useNavigate } from 'react-router-dom';


const MyNavbar = () => {
  const [scrolled, setScrolled] = useState(false);
  const [fullScreenDropdownVisible, setFullScreenDropdownVisible] = useState(false);
  const [fullScreenDropdownVisibleResources, setFullScreenDropdownVisibleResources] = useState(false);

  const navigate = useNavigate();

  let hoverTimeout;
  let hoverTimeoutResources;


  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 100) {
        setScrolled(true)
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const handleMouseEnter = () => {
    clearTimeout(hoverTimeout);
    setFullScreenDropdownVisible(true);
  };

  const handleMouseLeave = () => {
    hoverTimeout = setTimeout(() => {
      setFullScreenDropdownVisible(false);
    }, 200);
  };

  const handleMouseEnterResources = () => {
    clearTimeout(hoverTimeoutResources);
    setFullScreenDropdownVisibleResources(true);
  };

  const handleMouseLeaveResources = () => {
    hoverTimeoutResources = setTimeout(() => {
      setFullScreenDropdownVisibleResources(false);
    }, 200);
  };

  return (
    <div
      className={`w-100 ${scrolled ? "navbar-scrolled" : ""}`}
      style={{
        position: scrolled ? 'sticky' : 'relative',
        background: scrolled ? "linear-gradient(to bottom, #ffffff, #f0f0f0)" : "#FBFCFE",
        top: 0,
        zIndex: 1000,
      }}
    >
      <Navbar
        expand="xl"
        className={`container typography-p2 d-flex justify-content-center`}
      >
        <Container className="p-0 m-0">
          <Navbar.Brand as={NavLink} to="/">
            <img src={Logo} alt="Logo" className="logo" />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="pt-3">
              <Nav.Link as={NavLink} to="/" end>
                Home
              </Nav.Link>
              <Nav.Link as={NavLink} to="/about-us">
                About Us
              </Nav.Link>

              <NavDropdown
                title="Services"
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
              >
              </NavDropdown>

              <Nav.Link
                as={NavLink}
                to="/contact-us"
                style={{ marginRight: 0, paddingRight: 0 }}
              >
                Contact Us
              </Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

    </div>
  );
};

export default MyNavbar;
